package day3;

public class tablesprogram {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1; i<=10; i++){
			System.out.println("the table of "+i);
			for(int j=1; j<=10; j++){
				
				System.out.println(i+"*"+j+"="+i*j);
			}
			
		}

	}

}
